        #mc_agt_name_list = str(self.mcae1.get()).split(",")
        mc_agt_name_list = str(self.mcae1.get()).split(":")
        
        mc_agt_name = mc_agt_name_list[0]
        log.debug("mc_agt_interface : Value of mc_agt_name_list %s, mc_agt_name = %s\n"%(mc_agt_name_list, mc_agt_name))
       
        # Setting default values 
